import{_ as e,c as a,o as l,b0 as d}from"./chunks/framework.C5ev1SXp.js";const r="/scm-docs/assets/image-20240114141900907.naS2fG0p.png",n="/scm-docs/assets/image-20240114141925746.YKYfPA-j.png",i="/scm-docs/assets/image-20240114142010644.Ce8Fz6ZI.png",s="/scm-docs/assets/image-20240114142028173.BDbMG_XT.png",_=JSON.parse('{"title":"LV001-ATK-MC6625F模块简介","description":null,"frontmatter":{"title":"LV001-ATK-MC6625F模块简介","date":"2025-09-27T13:14:00.000Z","icon":"famicons:logo-markdown","permalink":"/sdoc/advanced-chapter/ov7725/126b09516b08191a7bd0fd02","index":true,"tags":null,"categories":null,"copyright":false,"keywords":null,"cover":null,"comments":null,"mathjax":null,"top":null,"description":null,"tdoc":{"detailDate":"2025-09-27T13:14:00.401Z","fulluuid":"a7bd0fd0290345cfae6ad4ff74f043f7","useduuid":"a7bd0fd02"}},"headers":[],"relativePath":"sdoc/advanced-chapter/ov7725/126b09516b08191a7bd0fd02.md","filePath":"sdoc/30-进阶篇/03-OV7725/LV001-ATK-MC6625F模块简介.md","lastUpdated":1758811394000}'),c={name:"sdoc/advanced-chapter/ov7725/126b09516b08191a7bd0fd02.md"};function o(p,t,m,g,h,F){return l(),a("div",null,[...t[0]||(t[0]=[d('<h1 id="lv001-atk-mc6625f模块简介" tabindex="-1">LV001-ATK-MC6625F模块简介 <a class="header-anchor" href="#lv001-atk-mc6625f模块简介" aria-label="Permalink to &quot;LV001-ATK-MC6625F模块简介&quot;">​</a></h1><h2 id="一、atk-mc6625f模块简介" tabindex="-1">一、ATK-MC6625F模块简介 <a class="header-anchor" href="#一、atk-mc6625f模块简介" aria-label="Permalink to &quot;一、ATK-MC6625F模块简介&quot;">​</a></h2><p>ATK-MC7725F 模块一款高性能 30W 高清摄像头模块。 该模块采用 OmniVision 公司的 OV7725 摄像头传感器作为核心， OV7725 是一颗 1/4 英寸 CMOS VGA（ 640*480）的图像传感器。 同时 ATK-MC7725F 模块集成了有源晶振和 FIFO 芯片， 这使得可以采用大部分的 MCU 控制该模块和读取图像数据。</p><p>ATK-MC6625F 模块具有如下特点：</p><ul><li><p>集成有源晶振，无需外部提供时钟</p></li><li><p>集成 FIFO 芯片，方便外部 MCU 读取图像</p></li><li><p>支持 VGA、 QVGA 和从 CIF 到 40*30 的各种尺寸输出</p></li><li><p>支持 RawRGB、 RGB（ GBR4:2:2、 RGB565、 RGB555、 RGB444）、 YUV（ 4:2:2）和 YCbCr（4:2:2） 格式输出</p></li><li><p>自动图像控制功能：自动曝光（AEC）、自动白平衡（ AWB）、自动消除灯光条纹、自动黑电平校准（ABLC）和自动带通滤波器（ABF）等</p></li><li><p>支持图像质量控制：色彩饱和度调节、色调调节、伽马校准、锐度和镜头校准等</p></li><li><p>支持图像缩放、平移和窗口设置</p></li><li><p>支持标准的 SCCB 接口，兼容 IIC 接口</p></li><li><p>自带嵌入式微处理器</p></li><li><p>高灵敏度、低电压，适合嵌入式应用</p></li></ul><h2 id="二、参数说明" tabindex="-1">二、参数说明 <a class="header-anchor" href="#二、参数说明" aria-label="Permalink to &quot;二、参数说明&quot;">​</a></h2><h3 id="_1-基本参数" tabindex="-1">1. 基本参数 <a class="header-anchor" href="#_1-基本参数" aria-label="Permalink to &quot;1. 基本参数&quot;">​</a></h3><img src="'+r+'" alt="image-20240114141900907" style="zoom:50%;"><h3 id="_2-电气参数" tabindex="-1">2. 电气参数 <a class="header-anchor" href="#_2-电气参数" aria-label="Permalink to &quot;2. 电气参数&quot;">​</a></h3><img src="'+n+'" alt="image-20240114141925746" style="zoom:50%;"><h2 id="三、引脚说明" tabindex="-1">三、引脚说明 <a class="header-anchor" href="#三、引脚说明" aria-label="Permalink to &quot;三、引脚说明&quot;">​</a></h2><p>ATK-MC7725F 模块通过 2*9 的排针（ 2.54mm 间距）同外部相连接：</p><img src="'+i+'" alt="image-20240114142010644" style="zoom:33%;"><p>ATK-MC7725F 模块的原理图 如下：</p><img src="'+s+'" alt="image-20240114142028173" style="zoom:50%;"><p>从上图可以看出， ATK-MC7725F 模块自带了有源晶振，用于产生 12MHz 的时钟作为OV7725 传感器的 XCLK 输入，并且 ATK-MC7725F 模块集成了 AL422B 芯片， AL422B 芯片是可以 FIFO 芯片， 该 FIFO 芯片的容量为 384K 字节，足以存储 2 帧 QVGA 图像数据。 ATK-MC7725F 模块通过一个 2*9 的排针（P1）同外部电路连接，各引脚的详细描述，如下表所示：</p><table tabindex="0"><thead><tr><th style="text-align:center;">序号</th><th style="text-align:center;">名称</th><th>说明</th></tr></thead><tbody><tr><td style="text-align:center;">1</td><td style="text-align:center;">3.3V</td><td>3.3V 电源输入</td></tr><tr><td style="text-align:center;">2</td><td style="text-align:center;">GND</td><td>电源地</td></tr><tr><td style="text-align:center;">3</td><td style="text-align:center;">WRST</td><td>FIFO 写复位信号输入</td></tr><tr><td style="text-align:center;">4</td><td style="text-align:center;">SCL</td><td>SCCB 通讯时钟信号</td></tr><tr><td style="text-align:center;">5</td><td style="text-align:center;">RRST</td><td>FIFO 读复位信号输入</td></tr><tr><td style="text-align:center;">6</td><td style="text-align:center;">SDA</td><td>SCCB 通讯通讯数据信号</td></tr><tr><td style="text-align:center;">7</td><td style="text-align:center;">OE</td><td>FIFO 输出使能信号输入</td></tr><tr><td style="text-align:center;">8~15</td><td style="text-align:center;">D0~D7</td><td>FIFO 数据输出端口</td></tr><tr><td style="text-align:center;">16</td><td style="text-align:center;">RCLK</td><td>FIFO 读时钟信号输入</td></tr><tr><td style="text-align:center;">17</td><td style="text-align:center;">VSYNC</td><td>帧同步信号</td></tr><tr><td style="text-align:center;">18</td><td style="text-align:center;">WEN</td><td>FIFO 写使能信号输入</td></tr></tbody></table>',17)])])}const x=e(c,[["render",o]]);export{_ as __pageData,x as default};
