import{_ as e,c,o as r,b0 as t}from"./chunks/framework.C5ev1SXp.js";const p=JSON.parse('{"title":"LV001-概述","description":null,"frontmatter":{"title":"LV001-概述","date":"2025-09-24T19:13:23.000Z","icon":"famicons:logo-markdown","permalink":"/sdoc/peripheral/fsmc/sram/126b09248e5b007fcadf7acc","index":true,"tags":null,"categories":null,"copyright":false,"keywords":null,"cover":null,"comments":null,"mathjax":null,"top":null,"description":null,"tdoc":{"detailDate":"2025-09-24T19:13:23.007Z","fulluuid":"fcadf7accc90458fa4cab2cebf963be3","useduuid":"fcadf7acc"}},"headers":[],"relativePath":"sdoc/peripheral/fsmc/sram/126b09248e5b007fcadf7acc.md","filePath":"sdoc/20-基本外设篇/25-FSMC/05-外部SRAM/LV001-概述.md","lastUpdated":1758153691000}'),l={name:"sdoc/peripheral/fsmc/sram/126b09248e5b007fcadf7acc.md"};function o(s,a,d,n,i,f){return r(),c("div",null,[...a[0]||(a[0]=[t('<h1 id="lv001-概述" tabindex="-1">LV001-概述 <a class="header-anchor" href="#lv001-概述" aria-label="Permalink to &quot;LV001-概述&quot;">​</a></h1><h2 id="一、什么是sram" tabindex="-1">一、什么是SRAM <a class="header-anchor" href="#一、什么是sram" aria-label="Permalink to &quot;一、什么是SRAM&quot;">​</a></h2><p>这个参考：<a href="/scm-docs/sdoc/basic/overview/126b08ba13b3329986816cf4.html">10-基础篇/01-概述/LV010-常见存储器.md</a></p><h2 id="二、为什么要扩展sram" tabindex="-1">二、为什么要扩展SRAM？ <a class="header-anchor" href="#二、为什么要扩展sram" aria-label="Permalink to &quot;二、为什么要扩展SRAM？&quot;">​</a></h2><p>STM32控制器芯片内部有一定大小的SRAM及FLASH作为内存和程序存储空间，程序下载的时候是存放在内部的FLASH中的，在程序运行过程中，会有一些变量或者其他的东西存放在SRAM中运行，这就跟我们PC电脑上的内存一样。当程序较大，内存和程序空间不足时，就需要在STM32芯片的外部扩展存储器了。STM32F103ZE系列芯片可以扩展外部SRAM用作内存。</p><p>给STM32芯片扩展内存与给PC扩展内存的原理是一样的，只是PC上一般以内存条的形式扩展，而且内存条实质是由多个内存颗粒(即SDRAM芯片)组成的通用标准模块，而STM32扩展时，直接直接与SRAM芯片连接。</p>',6)])])}const u=e(l,[["render",o]]);export{p as __pageData,u as default};
